import React from "react";
import {ImagePopup} from "./ImagePopup";

export function Card (props) {

    const [selectedCard, setSelectedCard] = React.useState(null);

    function handleCardImageClick(card) {
        setSelectedCard(card);
    }

    function closePopup() {
        setSelectedCard(null);
    }

    React.useEffect(() => {
        if (selectedCard) {
            const popupWithImage = document.querySelectorAll(`#popup_photo_${selectedCard._id}`);
            popupWithImage.forEach(popup => popup.classList.add('popup_opened'));
        }
    }, [selectedCard]);

    return(
        <section className="main__places places root__container">
            {props.cards.map((card) =>
                (
                    <>
                        <article className="card" key={card._id}>
                            <img alt=""  className="card__image" style={{backgroundImage: `url(${card.link})`}} onClick={handleCardImageClick}></img>
                            <div className="card__heading">
                                <h2 className="card__title">{card.name}</h2>
                                <div className="card__likes">
                                    <button type="button" className="card__like">{card.likes._id}</button>
                                    <span className="card__like-counter">{card.likes.length}</span>
                                </div>
                            </div>
                            <button type="button" className="card__delete"></button>
                        </article>
                        {selectedCard && selectedCard._id === card._id &&
                            <ImagePopup id={`${card._id}`} name={`${card.name}`} link={`${card.link}`} onClose={closePopup}></ImagePopup>
                        }
                    </>
                )
            )}
        </section>
    )
}
