import React from "react";
import {ImagePopup} from "./ImagePopup";

export function Card (props) {

    const [isPopupOpen, setIsPopupOpen] = React.useState(false);

    function handleCardImageClick() {
        if (isPopupOpen === false) {
            const popupWithImage = document.querySelector('#popup_photo')
            popupWithImage.classList.add('popup_opened')
            setIsPopupOpen(!isPopupOpen);
        }
    }

    function closePopup() {
        setIsPopupOpen(false);
    }

    return(
        <section className="main__places places root__container">

            {props.cards.map((card) =>
                (
                    <article className="card" key={card._id}>
                        <img alt=""  className="card__image" style={{backgroundImage: `url(${card.link})`}} onClick={handleCardImageClick}></img>
                        <div className="card__heading">
                            <h2 className="card__title">{card.name}</h2>
                            <div className="card__likes">
                                <button type="button" className="card__like">{card.likes._id}</button>
                                <span className="card__like-counter">{card.likes.length}</span>
                            </div>
                        </div>
                        <button type="button" className="card__delete"></button>
                        {isPopupOpen && <ImagePopup title={`${card.name}`} link={`${card.link}`}></ImagePopup>}
                    </article>
                ))}
        </section>
    )
}
